import React, { Component } from "react";
import "./QuanLyGioHang.css";
import { DataGrid } from "@mui/x-data-grid";
import { randomCreatedDate, randomUpdatedDate } from "@mui/x-data-grid";
import PendingActionsOutlinedIcon from "@mui/icons-material/PendingActionsOutlined";
import CancelOutlinedIcon from "@mui/icons-material/CancelOutlined";
import SpellcheckOutlinedIcon from "@mui/icons-material/SpellcheckOutlined";
import DoNotDisturbOnTotalSilenceOutlinedIcon from "@mui/icons-material/DoNotDisturbOnTotalSilenceOutlined";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
export default class QuanLyNguoiDung extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listOrders: [],
    };
  }
  componentDidUpdate(prevProps) {
    if (prevProps.allOrders !== this.props.allOrders) {
      this.setState({
        listOrders: this.props.allOrders.orderList,
      });
    }
  }
  componentDidMount() {
    this.props.getAllOrders();
  }
  render() {
    const columns = [
      { field: "idUser", width: 180 },
      { field: "category", width: 250 },
      {
        field: "image",
        width: 250,
      },
      { field: "createdAt", width: 180 },
      { field: "quantity", width: 180 },
      { field: "status", width: 180 },
    ];
    console.log(this.state.listOrders);
    const rows = [...this.state.listOrders];
    const rowss = [...getArr];
    return (
      <div className="container__order">
        <div className="orders__management">
          <div className="order order-pending">
            <span>Order Pending</span>
            <div className="order-pending-icon">
              <PendingActionsOutlinedIcon></PendingActionsOutlinedIcon>
            </div>{" "}
            <br />
            <span className="number">2</span>
          </div>
          <div className="order order-cancel">
            <span>Order Cancel</span>
            <div className="order-pending-icon">
              <CancelOutlinedIcon></CancelOutlinedIcon>
            </div>
            <br />
            <span className="number">2</span>
          </div>
          <div className="order order-process">
            <span>Order Process</span>
            <div className="order-pending-icon">
              <SpellcheckOutlinedIcon></SpellcheckOutlinedIcon>
            </div>
            <br />
            <span className="number">2</span>
          </div>
          <div className="order order-total">
            <span>Order Total</span>
            <div className="order-pending-icon">
              <DoNotDisturbOnTotalSilenceOutlinedIcon></DoNotDisturbOnTotalSilenceOutlinedIcon>
            </div>
            <br />
            <span className="number">2</span>
          </div>
        </div>
        <div className="container__order--list">
          <TableContainer component={Paper}>
            <Table
              sx={{ minWidth: 650, textAlign: "center" }}
              aria-label="caption table"
            >
              <caption>TOP CUSTOMERS</caption>
              <TableHead>
                <TableRow>
                  <TableCell>Top</TableCell>
                  <TableCell align="center">Name</TableCell>
                  <TableCell align="center">Email&nbsp;(g)</TableCell>
                  <TableCell align="center">Created At&nbsp;(g)</TableCell>
                  <TableCell align="center">Total&nbsp;($)</TableCell>
                  <TableCell align="center">Sticket&nbsp;($)</TableCell>
                </TableRow>
              </TableHead>
              <TableBody style={{ textAlign: "center" }}>
                {rowss.map((row) => (
                  <TableRow
                    className="row-username"
                    style={{ color: "red !important" }}
                    key={row.name}
                  >
                    <TableCell component="th" scope="row">
                      {row.top}
                    </TableCell>
                    <TableCell className="text-row" align="center">
                      {row.username}
                    </TableCell>
                    <TableCell className="text-row" align="center">
                      {row.email}
                    </TableCell>
                    <TableCell className="text-row" align="center">
                      {row.createdAt}
                    </TableCell>
                    <TableCell className="text-row" align="center">
                      {row.total} $
                    </TableCell>
                    <TableCell className="text-row" align="center">
                      <Button
                        className="edit"
                        variant="contained"
                        color="success"
                        className="success-btn"
                      >
                        Sticket
                      </Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </div>
      </div>
    );
  }
}
