import React, { Component } from "react";
import "./QuanLyGioHang.css";
import Paper from "@mui/material/Paper";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TablePagination from "@mui/material/TablePagination";
import TableRow from "@mui/material/TableRow";
export default class QuanLyNguoiDung extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listOrders: [],
    };
  }
  componentDidUpdate(prevProps) {
    if (prevProps.allOrders !== this.props.allOrders) {
      this.setState({
        listOrders: this.props.allOrders.orderList,
      });
    }
  }
  componentDidMount() {
    this.props.getAllOrders();
  }
  render() {
    const rows = [...this.state.listOrders];
    return (
      <div className="container__order">
        <div className="orders__management">
          <div className="order order-pending">
            <span>Order Pending</span>
            <div className="order-pending-icon">
              <PendingActionsOutlinedIcon></PendingActionsOutlinedIcon>
            </div>{" "}
            <br />
            <span className="number">2</span>
          </div>
          <div className="order order-cancel">
            <span>Order Cancel</span>
            <div className="order-pending-icon">
              <CancelOutlinedIcon></CancelOutlinedIcon>
            </div>
            <br />
            <span className="number">2</span>
          </div>
          <div className="order order-process">
            <span>Order Process</span>
            <div className="order-pending-icon">
              <SpellcheckOutlinedIcon></SpellcheckOutlinedIcon>
            </div>
            <br />
            <span className="number">2</span>
          </div>
          <div className="order order-total">
            <span>Order Total</span>
            <div className="order-pending-icon">
              <DoNotDisturbOnTotalSilenceOutlinedIcon></DoNotDisturbOnTotalSilenceOutlinedIcon>
            </div>
            <br />
            <span className="number">2</span>
          </div>
        </div>
        <div className="container__order--list">
          <Paper sx={{ width: "100%", overflow: "hidden" }}>
            <TableContainer sx={{ maxHeight: 440 }}>
              <Table stickyHeader aria-label="sticky table">
                <TableHead>
                  <TableRow>
                    {columns.map((column) => (
                      <TableCell
                        key={column.id}
                        align={column.align}
                        style={{ minWidth: column.minWidth }}
                      >
                        {column.label}
                      </TableCell>
                    ))}
                  </TableRow>
                </TableHead>
                <TableBody>
                  {rows
                    .map((row) => {
                      return (
                        <TableRow
                          hover
                          role="checkbox"
                          tabIndex={-1}
                          key={row.code}
                        >
                          {columns.map((column) => {
                            const value = row[column.id];
                            return (
                              <TableCell key={column.id} align={column.align}>
                                {column.format && typeof value === "number"
                                  ? column.format(value)
                                  : value}
                              </TableCell>
                            );
                          })}
                        </TableRow>
                      );
                    })}
                </TableBody>
              </Table>
            </TableContainer>
            <TablePagination
              rowsPerPageOptions={[10, 25, 100]}
              component="div"
              count={rows.length}
              rowsPerPage={rowsPerPage}
              page={page}
              onPageChange={handleChangePage}
              onRowsPerPageChange={handleChangeRowsPerPage}
            />
          </Paper>
        </div>
      </div>
    );
  }
}
