import React, { Component } from "react";
import { TextField, Button } from "@mui/material";
import { DataGrid } from "@mui/x-data-grid";
import { connect } from "react-redux";
import io from "socket.io-client";
import actions from "./../../actions/userActions";
import axios from "axios";
import "./Chat.css";
class Chat extends Component {
  constructor(props) {
    super(props);
    this.state = {
      message: "",
      id: 0,
      arr: [],
      isSeen: true,
    };
    this.socket = io(`localhost:9000/`, {
      transports: ["websocket", "polling", "flashsocket"],
    });
  }
  componentDidMount() {
    this.props.getAllUser();
    this.props.getAllMess();
    let idUser = [];
    this.props.listUser.forEach((element) => {
      idUser.push(element.id);
    });
    idUser.forEach((element) => {
      this.socket.emit("join_rom", element);
    });
    setTimeout(() => {
      let obj = {};
      let arrObj = [];
      this.props.chat.forEach((el, i) => {
        let result = arrObj.findIndex((e) => e.email === el.email);
        if (result === -1) {
          arrObj.push(el);
        } else {
          arrObj[result].email = el.email;
          arrObj[result].message = el.message;
          arrObj[result].id = el.id;
          arrObj[result].image = el.image;
          arrObj[result].active = el.active;
        }
      });
      this.setState({
        arr: arrObj,
      });
    }, 300);
    this.socket.on("message_hi", (message) => {
      let { arr } = this.state;
      let findIndex = arr.findIndex((e) => e.email === message.email);
      if (findIndex === -1) {
        arr.push(message);
      } else {
        arr[findIndex].username = message.username;
        arr[findIndex].message = message.message;
        arr[findIndex].id = message.id;
        arr[findIndex].image = message.image;
        arr[findIndex].active = true;
      }
      this.setState({
        arr: arr,
      });
      this.props.getAllMess();
    });
  }
  handleOnChange = (e) => {
    this.setState({
      message: e.target.value,
    });
  };
  hanleOnClick = (e) => {
    e.preventDefault();
    this.socket.emit("send_mess", {
      mess: this.state.message,
      id: this.state.id,
    });
  };
  handleRepMess = (id) => {
    axios
      .put(`http://localhost:9000/chat/${id}`)
      .then((res) => {})
      .catch((e) => console.log(e));

    setTimeout(() => {
      console.log(123);

      let obj = {};
      let arrObj = [];
      this.props.chat.forEach((el, i) => {
        let result = arrObj.findIndex((e) => e.email === el.email);
        if (result === -1) {
          arrObj.push(el);
        } else {
          arrObj[result].email = el.email;
          arrObj[result].message = el.message;
          arrObj[result].id = el.id;
          arrObj[result].image = el.image;
          arrObj[result].active = el.active;
        }
      });
      this.setState({
        arr: arrObj,
      });
    }, 500);
  };
  render() {
    console.log(this.props.chat);
    const rows = [...this.props.listUser];
    const columns = [
      { field: "id", headerName: "ID", width: 70, headerAlign: "center" },
      {
        field: "username",
        headerName: "Username",
        width: 100,
      },
      {
        field: "email",
        headerName: "Email",
        width: 200,
      },
      {
        field: "role",
        headerName: "Role",
        width: 100,
      },
      {
        field: "password",
        headerName: "Password",
        width: 100,
      },
      {
        field: "address",
        headerName: "Address",
        width: 100,
        type: "text",
      },
      {
        field: "numberphone",
        headerName: "Number Phone",
        width: 100,
        type: "text",
      },
      {
        field: "button",
        headerName: "Action",
        width: 200,

        renderCell: (cellValues) => {
          return (
            <>
              <Button
                className="edit"
                variant="contained"
                color="success"
                onClick={() => this.joinRoom(cellValues)}
              >
                SEND MESSAGE
              </Button>
            </>
          );
        },
        headerAlign: "center",
      },
    ];
    return (
      <div className="contasiner">
        <div className="form__chat">
          <h1>MESSENGER</h1>
          {this.state.arr.map((e) => {
            return (
              <div onClick={() => this.handleRepMess(e.id)} className="mess">
                <div className="avt">
                  <img src={e.image} />
                </div>
                <div className="content">
                  <div className="content-mess">
                    <h2 className="username">{e.username}</h2>
                    <span className={e.active ? `message isActive` : `message`}>
                      {e.message}
                    </span>
                  </div>
                  <div className={e.active ? `seen` : ""}></div>
                </div>
              </div>
            );
          })}
        </div>
        <div className="data">
          <DataGrid rows={rows} columns={columns} pageSize={4} />
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return { listUser: state.listUser, chat: state.chat };
};
const mapDispatchToProps = (dispatch) => {
  return {
    getAllUser: () => {
      dispatch(actions.getAllUser());
    },
    getAllMess: () => {
      dispatch(actions.getAllMess());
    },
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(Chat);
