import React, { Component } from "react";
import { TextField, Button } from "@mui/material";
import { DataGrid } from "@mui/x-data-grid";
import { withAlert } from "react-alert";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import CancelPresentationOutlinedIcon from "@mui/icons-material/CancelPresentationOutlined";
import AddOutlinedIcon from "@mui/icons-material/AddOutlined";
import "react-phone-number-input/style.css";
import PhoneInput from "react-phone-number-input";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";
import MenuItem from "@mui/material/MenuItem";
import InputLabel from "@mui/material/InputLabel";
import "./QuanLyNguoiDung.css";
class Chat extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: "",
      listUser: [],
      username: "",
      email: "",
      password: "",
      address: "",
      numberphone: "",
      role: "",
      toggleForm: true,
      isLoading: true,
    };
  }
 
  render() {
    const columns = [
      { field: "id", headerName: "ID", width: 70, headerAlign: "center" },
      {
        field: "username",
        headerName: "Username",
        width: 100,
      },
      {
        field: "email",
        headerName: "Email",
        width: 200,
      },
      {
        field: "role",
        headerName: "Role",
        width: 100,
      },
      {
        field: "password",
        headerName: "Password",
        width: 100,
      },
      {
        field: "address",
        headerName: "Address",
        width: 100,
        type: "text",
      },
      {
        field: "numberphone",
        headerName: "Number Phone",
        width: 100,
        type: "text",
      },
      {
        field: "button",
        headerName: "Action",
        width: 200,

        renderCell: (cellValues) => {
          return (
            <>
              <Button
                className="edit"
                variant="contained"
                color="success"
                onClick={() => this.handleUpdateUser(cellValues)}
              >
                EDIT
              </Button>
              <Button
                variant="contained"
                color="warning"
                onClick={() => this.handleClick(cellValues)}
              >
                DELETE
              </Button>
            </>
          );
        },
        headerAlign: "center",
      },
    ];

  
    const rowss = [...getArr];
    return (
      <div className="container">
        {this.state.toggleForm ? (
          <div className="form">
            <span className="text-controll">FORM COTROLL CUSTOMER</span>
            <CancelPresentationOutlinedIcon
              onClick={this.handleCloseForm}
              className="close-button"
            ></CancelPresentationOutlinedIcon>
            <TextField
              onChange={this.handleOnChange}
              className="input"
              id="outlined-basic"
              label="User Name"
              variant="standard"
              size="small"
              name="username"
              value={this.state.username}
            />
            <TextField
              onChange={this.handleOnChange}
              className="input"
              id="filled-basic"
              label="Email"
              variant="standard"
              size="small"
              name="email"
              aria-controls
              value={this.state.email}
            />
            <TextField
            
              onChange={this.handleOnChange}
              className="input"
              id="standard-basic"
              label="Pass word"
              variant="standard"
              size="small"
              name="password"
              type="password"
              value={this.state.password}
            />
            <TextField
              onChange={this.handleOnChange}
              className="input"
              id="standard-basic"
              label="Address"
              variant="standard"
              size="small"
              name="address"
              value={this.state.address}
            />
            <FormControl variant="standard" sx={{ m: 1, minWidth: 120 }}>
              <InputLabel id="demo-simple-select-standard-label">
                Role
              </InputLabel>
              <Select
                labelId="demo-simple-select-standard-label"
                id="demo-simple-select-standard"
                label="Age"
                onChange={this.handleOnChange}
                name="role"
                value={this.state.role}
              >
                <MenuItem value="customer" selected={true}>
                  customer
                </MenuItem>
                <MenuItem value="admin">admin</MenuItem>
              </Select>
            </FormControl>
            <div className="form-group">
              <label htmlFor="numberphone">Number Phone</label>
              <PhoneInput
                placeholder="Enter phone number"
                onChange={this.handleGetNumberPhone}
                value={this.state.numberphone}
              />
            </div>

            <Button
              variant="contained"
              color="success"
              onClick={this.createNewUser}
              className="success-btn"
            >
              {this.state.id ? `Update` : `Register`}
            </Button>
          </div>
        ) : (
          ""
        )}
        {this.state.isLoading ? (
          `Loading Data . . . Please waiting !!! `
        ) : (
          <>
            <div className="data">
              <Button
                onClick={this.handleToggleForm}
                style={{ marginBottom: "20px" }}
                className="edit"
                variant="contained"
                color="success"
              >
                Add Account
                <AddOutlinedIcon style={{ paddingLeft: "10px" }} />
              </Button>
              <DataGrid rows={rows} columns={columns} pageSize={4} />
            </div>
            <div className="user">
              <div className="hot-customer">
                <span className="top-customer">TOP CUSTOMER :v</span>
                {/* {getArr.map((user) => {
                  return (
                    <div className="custommer">
                      <h4>{user.top}</h4>
                      <div className="custommer__name">{user.name}</div>
                      <div className="custommer__email">{user.email}</div>
                      <div className="custommer__creadAt">{user.createdAt}</div>
                      <div className="custommer__total">{user.total}</div>
                    </div>
                  );
                })} */}
                <TableContainer component={Paper}>
                  <Table
                    sx={{ minWidth: 650, textAlign: "center" }}
                    aria-label="caption table"
                  >
                    <caption>TOP CUSTOMERS</caption>
                    <TableHead>
                      <TableRow>
                        <TableCell>Top</TableCell>
                        <TableCell align="center">Name</TableCell>
                        <TableCell align="center">Email&nbsp;(g)</TableCell>
                        <TableCell align="center">
                          Created At&nbsp;(g)
                        </TableCell>
                        <TableCell align="center">Total&nbsp;($)</TableCell>
                        <TableCell align="center">Sticket&nbsp;($)</TableCell>
                      </TableRow>
                    </TableHead>
                    <TableBody style={{ textAlign: "center" }}>
                      {rowss.map((row) => (
                        <TableRow
                          className="row-username"
                          style={{ color: "red !important" }}
                          key={row.name}
                        >
                          <TableCell component="th" scope="row">
                            {row.top}
                          </TableCell>
                          <TableCell className="text-row" align="center">
                            {row.username}
                          </TableCell>
                          <TableCell className="text-row" align="center">
                            {row.email}
                          </TableCell>
                          <TableCell className="text-row" align="center">
                            {row.createdAt}
                          </TableCell>
                          <TableCell className="text-row" align="center">
                            {row.total} $
                          </TableCell>
                          <TableCell className="text-row" align="center">
                            <Button
                              className="edit"
                              variant="contained"
                              color="success"
                              className="success-btn"
                            >
                              Sticket
                            </Button>
                          </TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                </TableContainer>
                ;
              </div>
              <div className="active-customer">
                <h3>New Customer</h3>
                <DataGrid
                  rows={rows}
                  columns={columns}
                  pageSize={6}
                  autoHeight
                />
              </div>
            </div>
          </>
        )}
      </div>
    );
  }
}
export default withAlert()(Chat);
