import React, { Component } from "react";
import { Link } from "react-router-dom";
import "./Header.css";
import PeopleAltOutlinedIcon from "@mui/icons-material/PeopleAltOutlined";
import ShoppingCartOutlinedIcon from "@mui/icons-material/ShoppingCartOutlined";
import PostAddOutlinedIcon from "@mui/icons-material/PostAddOutlined";
import TuneOutlinedIcon from "@mui/icons-material/TuneOutlined";
import SmsOutlinedIcon from "@mui/icons-material/SmsOutlined";
export default class Header extends Component {
  handleChangeCss = (eev) => {
    let linkItem = document.querySelectorAll(".link-item");
    // linkItem.forEach((e) => {
    //   console.log(e);
    // });
  };
  render() {
    let customLink = [
      {
        text: "Quản Lý Người Dùng",
        to: "/quanlynguoidung",
        icon: <PeopleAltOutlinedIcon></PeopleAltOutlinedIcon>,
      },
      {
        text: "Quản Lý Giỏ Hàng",
        to: "/quanlygiohang",
        icon: <ShoppingCartOutlinedIcon></ShoppingCartOutlinedIcon>,
      },
      {
        text: "Quản Lý Sản Phẩm",
        to: "/quanlysanpham",
        icon: <PostAddOutlinedIcon></PostAddOutlinedIcon>,
      },
      {
        text: "Quản Lý Slider",
        to: "/quanlygiohang",
        icon: <TuneOutlinedIcon></TuneOutlinedIcon>,
      },
      {
        text: "Quản Lý Phản Hồi",
        to: "/quanlygiohang",
        icon: <SmsOutlinedIcon></SmsOutlinedIcon>,
      },
    ];
    return (
      <>
        <div className="headerMenu">
          <ul className="">
            {customLink.map((link, index) => {
              return (
                <li onClick={this.handleAddCss}>
                  <div className="icon-header">
                    {link.icon}
                    <Link
                      onClick={this.handleChangeCss}
                      key={index}
                      className={`link-item ${link.text}`}
                      to={link.to}
                    >
                      {link.text}
                    </Link>
                  </div>
                </li>
              );
            })}
          </ul>
        </div>
      </>
    );
  }
}
