import React, { Component } from "react";
import CancelPresentationOutlinedIcon from "@mui/icons-material/CancelPresentationOutlined";
import Pagination from "react-js-pagination";
import { TextField, Button, TextareaAutosize } from "@mui/material";
import "./QuanLySanPham.css";
export default class QuanLySanPham extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: "",
      title: "",
      price: "",
      description: "",
      image: "",
      activePage: 1,
    };
  }
  componentDidMount() {
    let pagination = document.querySelector(".pagination");
    window.addEventListener("scroll", () => {
      if (window.pageYOffset > 100) {
        pagination.classList.add("activePag");
      } else {
      }
    });
  }
  handlePageChange = (numberPage) => {
    this.setState({
      activePage: numberPage,
    });
    let token = JSON.parse(localStorage.getItem("token"));
    this.props.handlePageChange(numberPage, token);
  };
  deleteProduct = (id) => {
    this.props.deleteProduct(id);
    console.log();
    let numberPage = this.state.activePage;
    setTimeout(() => {
      this.props.handlePageChange(numberPage);
    }, 500);
  };
  updateProduct = (product) => {
    let { id, title, price, description, image } = product;
    this.setState({ id, title, price, description, image });
  };
  renderProducts = () => {
    return this.props.allProducts.map((product) => {
      return (
        <div className="card">
          <img src={product.image} />
          <h3>{product.title}</h3>
          <h2>${product.price} </h2>
          <span>{product.description.slice(0, 100)}</span>
          <div className="button-gr">
            <button
              onClick={() => this.deleteProduct(product.id)}
              type="button"
              class="btn-delete"
            >
              DELETE
            </button>
            <button
              onClick={() => this.updateProduct(product)}
              type="button"
              class="btn-update"
            >
              EDIT
            </button>
          </div>
        </div>
      );
    });
  };
  handleGetImg = (e) => {
    let data = new FormData();
    data.append("file", e.target.files[0]);
    data.append("upload_preset", "nguyenthanhtung");
    fetch(`https://api.cloudinary.com/v1_1/artimate/image/upload`, {
      method: "POST",
      body: data,
    })
      .then((res) => res.json())
      .then((data) =>
        this.setState({
          image: data.secure_url,
        })
      )
      .catch((e) => console.log(e));
  };
  handleGetValue = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };
  handleUpdateProducts = (e) => {
    e.preventDefault();
    let { title, description, price, image } = this.state;
this.setState({

})
    this.props.handleUpdateProducts({  });
  };
  render() {
    let { title, price, description, image } = this.state;
    return (
      <>
        <div className="container">
          <div className="form">
            <span className="text-controll">FORM COTROLL PRODUCTS</span>
            <CancelPresentationOutlinedIcon
              onClick={this.handleCloseForm}
              className="close-button"
            ></CancelPresentationOutlinedIcon>
            <TextField
              className="input"
              id="standard-basic"
              size="small"
              type="file"
              name="image"
              onChange={this.handleGetImg}
              value={image}
            />
            <TextField
              className="input"
              id="outlined-basic"
              label="Title"
              variant="standard"
              size="small"
              name="title"
              onChange={this.handleGetValue}
              value={title}
            />

            <TextField
              className="input"
              id="standard-basic"
              label="Price ($)"
              variant="standard"
              size="small"
              name="price"
              onChange={this.handleGetValue}
              value={price}
            />
            <TextField
              className="input"
              id="filled-basic"
              label="Description"
              variant="standard"
              size="small"
              name="description"
              aria-controls
              onChange={this.handleGetValue}
              value={description}
            />

            <TextField className="input" size="small" type="file" multiline />
            <Button
              onClick={this.handleUpdateProducts}
              className="edit"
              variant="contained"
              color="success"
              className="success-btn"
            >
              ADD PRODUCT
            </Button>
          </div>
          <div className="products">{this.renderProducts()}</div>
          <div className="pagination">
            <Pagination
              activePage={this.state.activePage}
              itemsCountPerPage={30}
              totalItemsCount={450}
              onChange={this.handlePageChange.bind(this)}
              pageRangeDisplayed={5}
            />
          </div>
        </div>
      </>
    );
  }
}
