import React, { Component } from "react";
import { TextField, Button } from "@mui/material";
import { DataGrid } from "@mui/x-data-grid";
import "./QuanLyNguoiDung.css";
export default class QuanLyNguoiDung extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listUser: [],
    };
  }
  componentDidMount() {
    this.props.getAllUser();
  }
  handleClick = (value) => {
    let { id } = value.row;
    this.props.deleteUser(id);
  };
  componentDidUpdate(prevProps) {
    if (prevProps.listUser !== this.props.listUser) {
      this.setState({
        listUser: [...this.props.listUser],
      });
    }
  }
  render() {
    console.log(this.state.listUser);

    const columns = [
      { field: "id", headerName: "ID", width: 70, headerAlign: "center" },
      {
        field: "username",
        headerName: "Username",
        width: 200,
        headerAlign: "center",
      },
      {
        field: "email",
        headerName: "Email",
        width: 200,
        headerAlign: "center",
      },
      {
        field: "password",
        headerName: "Password",
        width: 100,
        headerAlign: "center",
      },
      {
        field: "createAt",
        headerName: "Createday",
        width: 100,
        headerAlign: "center",
      },
      {
        field: "button",
        headerName: "Action",
        width: 200,
        type: "text",
        renderCell: (cellValues) => {
          return (
            <>
              <Button
                className="edit"
                variant="contained"
                color="success"
                onClick={() => this.handleClick(cellValues)}
              >
                Sửa
              </Button>
              <Button
                variant="contained"
                color="warning"
                onClick={() => this.handleClick(cellValues)}
              >
                Xóa
              </Button>
            </>
          );
        },
        headerAlign: "center",
      },
    ];

    const rows = [...this.state.listUser];
    return (
      <div className="container">
        <div className="form">
          <TextField
            className="input"
            id="outlined-basic"
            label="Outlined"
            variant="outlined"
            size="small"
          />
          <TextField
            className="input"
            id="filled-basic"
            label="Filled"
            variant="outlined"
            size="small"
          />
          <TextField
            className="input"
            id="standard-basic"
            label="Standard"
            variant="outlined"
            size="small"
          />
          <Button variant="outlined">Đăng Kí</Button>
        </div>
        <div className="data">
          <DataGrid rows={rows} columns={columns} pageSize={8} />
        </div>
      </div>
    );
  }
}
