import React, { Component } from "react";
import CancelPresentationOutlinedIcon from "@mui/icons-material/CancelPresentationOutlined";
import Pagination from "react-js-pagination";
import { TextField, Button, TextareaAutosize } from "@mui/material";
import "./QuanLySanPham.css";
export default class QuanLySanPham extends Component {
  constructor(props) {
    super(props);
    this.state = {
      activePage: 1,
    };
  }
  handlePageChange = (numberPage) => {
    this.setState({
      activePage: numberPage,
      value: "",
      isLoading: true,
    });
    setTimeout(() => {
      this.setState({
        isLoading: false,
      });
    }, 1000);

    let token = JSON.parse(localStorage.getItem("token"));
    this.props.handlePageChange(numberPage, token);
  };
  renderProducts = () => {
    return this.props.allProducts.map((product) => {
      return (
        <div className="card">
          <img src={product.image} />
          <h3>{product.title}</h3>
          <h2>{product.price}</h2>
          <span>{product.description}</span>
        </div>
      );
    });
  };
  render() {
    return (
      <>
        <div className="container">
          <div className="form">
            <span className="text-controll">FORM COTROLL PRODUCTS</span>
            <CancelPresentationOutlinedIcon
              onClick={this.handleCloseForm}
              className="close-button"
            ></CancelPresentationOutlinedIcon>
            <TextField
              className="input"
              id="outlined-basic"
              label="Title"
              variant="standard"
              size="small"
              name="title"
            />

            <TextField
              className="input"
              id="standard-basic"
              label="Price ($)"
              variant="standard"
              size="small"
              name="price"
            />
            <TextField
              className="input"
              id="filled-basic"
              label="Description"
              variant="standard"
              size="small"
              name="description"
              aria-controls
            />
            <TextField
              className="input"
              id="standard-basic"
              size="small"
              type="file"
            />
            <TextField className="input" size="small" type="file" multiline />
            <Button
              className="edit"
              variant="contained"
              color="success"
              className="success-btn"
            >
              ADD PRODUCT
            </Button>
          </div>
          <div className="products">{this.renderProducts()}</div>
          <div className="pagination">
            <Pagination
              activePage={2}
              itemsCountPerPage={30}
              totalItemsCount={450}
              pageRangeDisplayed={5}
            />
          </div>
        </div>
      </>
    );
  }
}
