import React, { Component } from "react";
import { TextField, Button } from "@mui/material";
import { DataGrid } from "@mui/x-data-grid";

import { withAlert } from "react-alert";
import { withRouter } from "react-router-dom";
class QuanLyNguoiDung extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listUser: [],
      username: "",
      email: "",
      password: "",
    };
  }
  componentDidMount() {
    this.props.getAllUser();
  }
  handleClick = (value) => {
    let { id } = value.row;
    this.props.deleteUser(id);
  };
  componentDidUpdate(prevProps) {
    if (prevProps.listUser !== this.props.listUser) {
      this.setState({
        listUser: [...this.props.listUser],
      });
    }
  }
  handleOnChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };
  createNewUser = (e) => {
    e.preventDefault();
    let { username, email, password } = this.state;
    this.props.createNewUser({ username, email, password });
    setTimeout(() => {
      this.props.getAllUser();
    }, 300);
  };
  render() {
    const columns = [
      { field: "id", headerName: "ID", width: 70, headerAlign: "center" },
      {
        field: "username",
        headerName: "Username",
        width: 200,
        headerAlign: "center",
      },
      {
        field: "email",
        headerName: "Email",
        width: 200,
        headerAlign: "center",
      },
      {
        field: "password",
        headerName: "Password",
        width: 100,
        headerAlign: "center",
      },
      {
        field: "createdAt",
        headerName: "Createday",
        width: 100,
        headerAlign: "center",
        type: "text",
      },
      {
        field: "button",
        headerName: "Action",
        width: 200,

        renderCell: (cellValues) => {
          return (
            <>
              <Button
                className="edit"
                variant="contained"
                color="success"
                onClick={() => this.handleClick(cellValues)}
              >
                Sửa
              </Button>
              <Button
                variant="contained"
                color="warning"
                onClick={() => this.handleClick(cellValues)}
              >
                Xóa
              </Button>
            </>
          );
        },
        headerAlign: "center",
      },
    ];

    const rows = [...this.state.listUser];
    return (
      <div className="container">
        <div className="form">
          <TextField
            onChange={this.handleOnChange}
            className="input"
            id="outlined-basic"
            label="User Name"
            variant="outlined"
            size="small"
            name="username"
          />
          <TextField
            onChange={this.handleOnChange}
            className="input"
            id="filled-basic"
            label="Email"
            variant="outlined"
            size="small"
            name="email"
          />
          <TextField
            onChange={this.handleOnChange}
            className="input"
            id="standard-basic"
            label="Pass word"
            variant="outlined"
            size="small"
            name="password"
          />
          <Button onClick={this.createNewUser} variant="outlined" size="small">
            Đăng Kí
          </Button>
        </div>
        <div className="data">
          <DataGrid
            rows={rows}
            columns={columns}
            pageSize={5}
            checkboxSelection
          />
        </div>
      </div>
    );
  }
}
export default withAlert()(QuanLyNguoiDung);
