import React, { Component } from "react";
import { TextField, Button } from "@mui/material";
import { DataGrid } from "@mui/x-data-grid";
import { connect } from "react-redux";
import io from "socket.io-client";
import actions from "./../../actions/userActions";
import "./Chat.css";
class Chat extends Component {
  constructor(props) {
    super(props);
    this.state = {
      message: "",
    };
    this.socket = io(`localhost:9000/`, {
      transports: ["websocket", "polling", "flashsocket"],
    });
  }
  componentDidMount() {
    this.props.getAllUser();
  }

  render() {
    const rows = [...this.props.listUser];
    const columns = [
      { field: "id", headerName: "ID", width: 70, headerAlign: "center" },
      {
        field: "username",
        headerName: "Username",
        width: 100,
      },
      {
        field: "email",
        headerName: "Email",
        width: 200,
      },
      {
        field: "role",
        headerName: "Role",
        width: 100,
      },
      {
        field: "password",
        headerName: "Password",
        width: 100,
      },
      {
        field: "address",
        headerName: "Address",
        width: 100,
        type: "text",
      },
      {
        field: "numberphone",
        headerName: "Number Phone",
        width: 100,
        type: "text",
      },
      {
        field: "button",
        headerName: "Action",
        width: 200,

        renderCell: (cellValues) => {
          return (
            <>
              <Button
                className="edit"
                variant="contained"
                color="success"
                onClick={() => this.handleUpdateUser(cellValues)}
              >
                SEND MESSAGE
              </Button>
            </>
          );
        },
        headerAlign: "center",
      },
    ];
    return (
      <div className="container">
        <div className="form__chat">
          <div className="tool">
            <TextField onChange={this.handleOnChan} className="textF" />
            <Button color="success" variant="contained">
              SEND MESSAGE
            </Button>
          </div>
        </div>
        <>
          <div className="data">
            <DataGrid rows={rows} columns={columns} pageSize={4} />
          </div>
        </>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return { listUser: state.listUser };
};
const mapDispatchToProps = (dispatch) => {
  return {
    getAllUser: () => {
      dispatch(actions.getAllUser());
    },
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(Chat);
