import React, { Component } from "react";
import { TextField, Button } from "@mui/material";
import { DataGrid } from "@mui/x-data-grid";
import { withAlert } from "react-alert";
import CancelPresentationOutlinedIcon from "@mui/icons-material/CancelPresentationOutlined";
import "./QuanLyNguoiDung.css";
class QuanLyNguoiDung extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listUser: [],
      username: "",
      email: "",
      password: "",
      toggleForm: false,
      isLoading: true,
    };
  }
  componentDidMount() {
    this.props.getAllUser();
    this.props.getHotCustomer();
  }
  callFunctionHandleMessage = () => {
    setTimeout(() => {
      this.props.getAllUser();
      let { statusCode, message } = this.props.message;
      const alert = this.props.alert;
      if (statusCode === 200) {
        alert.success(message);
        this.setState({
          username: "",
          email: "",
          password: "",
          id: "",
        });
        return;
      }
      alert.error(message);
    }, 300);
  };
  handleClick = (value) => {
    let { id } = value.row;
    this.props.deleteUser(id);
    this.callFunctionHandleMessage();
  };

  componentDidUpdate(prevProps) {
    if (prevProps.listUser !== this.props.listUser) {
      this.setState({
        listUser: [...this.props.listUser],
        isLoading: false,
      });
    }
  }
  handleOnChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };
  createNewUser = (e) => {
    e.preventDefault();
    let { id, username, email, password } = this.state;
    if (!id) {
      this.props.createNewUser({ username, email, password });
      this.callFunctionHandleMessage();
      return;
    }
    this.props.handleUpdateUser({ id, username, email, password });
    this.callFunctionHandleMessage();
  };
  handleUpdateUser = (value) => {
    this.setState({
      toggleForm: true,
    });
    let { id, email, username, password } = value.row;
    console.log({ id, email, username, password });
    this.setState({
      id,
      email,
      username,
      password,
    });
  };
  handleToggleForm = () => {
    this.setState({
      toggleForm: !this.state.toggleForm,
    });
  };
  handleCloseForm = () => {
    this.setState({
      toggleForm: false,
    });
  };
  render() {
    const columns = [
      { field: "id", headerName: "ID", width: 70, headerAlign: "center" },
      {
        field: "username",
        headerName: "Username",
        width: 200,
        headerAlign: "center",
      },
      {
        field: "email",
        headerName: "Email",
        width: 200,
        headerAlign: "center",
      },
      {
        field: "password",
        headerName: "Password",
        width: 100,
        headerAlign: "center",
      },
      {
        field: "createdAt",
        headerName: "Createday",
        width: 100,
        headerAlign: "center",
        type: "text",
      },
      {
        field: "button",
        headerName: "Action",
        width: 200,

        renderCell: (cellValues) => {
          return (
            <>
              <Button
                className="edit"
                variant="contained"
                color="success"
                onClick={() => this.handleUpdateUser(cellValues)}
              >
                Sửa
              </Button>
              <Button
                variant="contained"
                color="warning"
                onClick={() => this.handleClick(cellValues)}
              >
                Xóa
              </Button>
            </>
          );
        },
        headerAlign: "center",
      },
    ];
    const rows = [...this.state.listUser.reverse()];
    let { hotCus } = this.props;
    let getKey = [];
    for (const key in hotCus) {
      getKey.push([key, hotCus[key]]);
    }
    let sortKey = [...getKey.sort((a, b) => b[1] - a[1])];
    console.log(sortKey);
    let getArr = [];
    sortKey.forEach((e) => {
      c
      // let mang = this.state.listUser.filter(parseInt(e[0]));
      // getArr.push(mang);
    });
    console.log(getArr);

    // let arrCustomer = [];
    // sortKey.forEach((element) => {

    // });

    return (
      <div className="container">
        {this.state.toggleForm ? (
          <div className="form">
            <CancelPresentationOutlinedIcon
              onClick={this.handleCloseForm}
              className="close-button"
            ></CancelPresentationOutlinedIcon>
            <TextField
              onChange={this.handleOnChange}
              className="input"
              id="outlined-basic"
              label="User Name"
              variant="outlined"
              size="small"
              name="username"
              value={this.state.username}
            />
            <TextField
              onChange={this.handleOnChange}
              className="input"
              id="filled-basic"
              label="Email"
              variant="outlined"
              size="small"
              name="email"
              aria-controls
              value={this.state.email}
            />
            <TextField
              onChange={this.handleOnChange}
              className="input"
              id="standard-basic"
              label="Pass word"
              variant="outlined"
              size="small"
              name="password"
              value={this.state.password}
            />
            <Button
              onClick={this.createNewUser}
              variant="outlined"
              size="small"
              class="register-button"
            >
              {this.state.id ? `Update` : `Register`}
            </Button>
          </div>
        ) : (
          ""
        )}
        {this.state.isLoading ? (
          `Loading Data . . . Please waiting !!! `
        ) : (
          <>
            <div className="data">
              <Button
                onClick={this.handleToggleForm}
                style={{ marginBottom: "20px" }}
                className="edit"
                variant="contained"
                color="success"
              >
                Add Account
              </Button>
              <DataGrid
                rows={rows}
                columns={columns}
                pageSize={5}
                checkboxSelection
              />
            </div>
            <div className="user">
              <div className="hot-customer">
                <h2>Gương Mặt Thân Quen :v</h2>
              </div>
              <div className="active-customer">
                <h2>Khách Hàng Đang Hoạt Động :v</h2>
              </div>
            </div>
          </>
        )}
      </div>
    );
  }
}
export default withAlert()(QuanLyNguoiDung);
