import React, { Component } from "react";
import CancelPresentationOutlinedIcon from "@mui/icons-material/CancelPresentationOutlined";
import { DataGrid } from "@mui/x-data-grid";
import { TextField, Button } from "@mui/material";
export default class QuanLyPhanHoi extends Component {
  render() {
    const columns = [
      { field: "id", headerName: "ID", width: 70, headerAlign: "center" },
      {
        field: "username",
        headerName: "Username",
        width: 200,
      },
      {
        field: "email",
        headerName: "Email",
        width: 200,
      },
      {
        field: "password",
        headerName: "Password",
        width: 100,
      },
      {
        field: "address",
        headerName: "Address",
        width: 100,
        type: "text",
      },
      {
        field: "numberphone",
        headerName: "Number Phone",
        width: 100,
        type: "text",
      },
      {
        field: "button",
        headerName: "Action",
        width: 200,

        renderCell: (cellValues) => {
          return (
            <>
              <Button
                className="edit"
                variant="contained"
                color="success"
                onClick={() => this.handleUpdateUser(cellValues)}
              >
                Sửa
              </Button>
              <Button
                variant="contained"
                color="warning"
                onClick={() => this.handleClick(cellValues)}
              >
                Xóa
              </Button>
            </>
          );
        },
        headerAlign: "center",
      },
    ];
    const rows = [...this.props.listUser.reverse()];

    return (
      <>
        <div className="form">
          <span className="text-controll">FORM COTROLL CUSTOMER</span>
          <CancelPresentationOutlinedIcon
            onClick={this.handleCloseForm}
            className="close-button"
          ></CancelPresentationOutlinedIcon>
          <TextField
            onChange={this.handleOnChange}
            className="input"
            id="outlined-basic"
            label="User Name"
            variant="standard"
            size="small"
            name="username"
          />
          <TextField
            onChange={this.handleOnChange}
            className="input"
            id="filled-basic"
            label="Email"
            variant="standard"
            size="small"
            name="email"
            aria-controls
          />
          <TextField
            onChange={this.handleOnChange}
            className="input"
            id="standard-basic"
            label="Pass word"
            variant="standard"
            size="small"
            name="password"
          />
          <TextField
            onChange={this.handleOnChange}
            className="input"
            id="standard-basic"
            label="Address"
            variant="standard"
            size="small"
            name="address"
          />
          <Button
            variant="contained"
            color="success"
            onClick={this.createNewUser}
            className="success-btn"
          >
            SEND
          </Button>
        </div>
        <DataGrid
          rows={rows}
          columns={columns}
          pageSize={4}
          checkboxSelection
        />
      </>
    );
  }
}
