import React, { Component } from "react";
import { Button, TextField } from "@mui/material";
import { DataGrid } from "@mui/x-data-grid";
import { connect } from "react-redux";
import io from "socket.io-client";
import actions from "./../../actions/userActions";
import CloseIcon from "@mui/icons-material/Close";
import "./Chat.css";
import axios from "axios";
class Chat extends Component {
  constructor(props) {
    super(props);
    this.state = {
      message: "",
      arr: [],
      isSeen: true,
      idUser: 0,
      message: "",
      username: "",
      image: "",
    };
    this.socket = io(`localhost:9000/`, {
      transports: ["websocket", "polling", "flashsocket"],
    });
  }
  componentDidMount() {
    this.props.getAllUser();
    this.props.getAllMess();
    let idUser = [];
    this.props.listUser.forEach((element) => {
      idUser.push(element.id);
    });
    idUser.forEach((element) => {
      this.socket.emit("join_rom", element);
    });
    setTimeout(() => {
      let obj = {};
      let arrObj = [];
      this.props.chat.forEach((el, i) => {
        let result = arrObj.findIndex((e) => e.email === el.email);
        if (result === -1) {
          arrObj.push(el);
        } else {
          arrObj[result].email = el.email;
          arrObj[result].message = el.message;
          arrObj[result].id = el.id;
          arrObj[result].image = el.image;
          arrObj[result].active = el.active;
        }
      });
      this.setState({
        arr: arrObj,
      });
    }, 300);
    this.socket.on("message_hi", (message) => {
      let { arr } = this.state;
      let findIndex = arr.findIndex((e) => e.email === message.email);
      if (findIndex === -1) {
        arr.push(message);
      } else {
        arr[findIndex].username = message.username;
        arr[findIndex].message = message.message;
        arr[findIndex].id = message.id;
        arr[findIndex].image = message.image;
        arr[findIndex].active = true;
      }
      this.setState({
        arr: arr,
      });
    });
    console.log(123);
  }
  handleOnChange = (e) => {
    this.setState({
      message: e.target.value,
    });
  };
  hanleOnClick = (e) => {
    e.preventDefault();
    this.socket.emit("send_mess", {
      mess: this.state.message,
      id: this.state.id,
    });
  };
  handleRepMess = (id, ) => {
    this.props.handleRepMess(id);
    setTimeout(() => {
      let { arr } = this.state;
      let findId = this.state.arr.findIndex((e) => e.id === id);
      arr[findId].active = false;
      this.setState({
        arr: arr,
        username: arr[findId].username,
        image: arr[findId].image,
      });
    }, 300);
    axios
      .get(`http://localhost:9000/chat?id=${id}`)
      .then((res) => {
        console.log(res.data);
      })
      .catch((e) => console.log(e));
  };
  render() {
    return (
      <div className="contasiner">
        <div className="form__chat">
          <h1>MESSENGER</h1>
          {this.state.arr.map((e) => {
            return (
              <div onClick={() => this.handleRepMess(e.id, e.idUser)} className="mess">
                <div className="avt">
                  <img src={e.image} />
                </div>
                <div className="content">
                  <div className="content-mess">
                    <h2 className="username">{e.username}</h2>
                    <span className={e.active ? `message isActive` : `message`}>
                      {e.message}
                    </span>
                  </div>
                  <div className={e.active ? `seen` : ""}></div>
                </div>
              </div>
            );
          })}
        </div>
        <div className="data">
          <div className="app">
            <div className="chat__realtime">
              <div className="info">
                <div className="img">
                  <img src={this.state.image} />
                </div>
                <div className="name">
                  <h3>{this.state.username}</h3>
                </div>
              </div>
              <div className="close-icon">
                <CloseIcon />
              </div>
            </div>
            <div className="input__message">
              <TextField
                className="input"
                id="standard-basic"
                label="Address"
                variant="standard"
                size="small"
                name="address"
              />
              <Button
                variant="contained"
                color="success"
                onClick={this.createNewUser}
              >
                SEND
              </Button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return { listUser: state.listUser, chat: state.chat };
};
const mapDispatchToProps = (dispatch) => {
  return {
    getAllUser: () => {
      dispatch(actions.getAllUser());
    },
    getAllMess: () => {
      dispatch(actions.getAllMess());
    },
    handleRepMess: (id) => {
      dispatch(actions.handleRepMess(id));
    },
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(Chat);
