import React, { Component } from "react";
import { TextField, Button } from "@mui/material";
import { DataGrid } from "@mui/x-data-grid";
import { connect } from "react-redux";
import io from "socket.io-client";
import actions from "./../../actions/userActions";
import "./Chat.css";
class Chat extends Component {
  constructor(props) {
    super(props);
    this.state = {
      message: "",
      id: 0,
      arr: [],
    };
    this.socket = io(`localhost:9000/`, {
      transports: ["websocket", "polling", "flashsocket"],
    });
  }
  componentDidMount() {
    this.props.getAllUser();
    let idUser = [];
    this.props.listUser.forEach((element) => {
      idUser.push(element.id);
    });
    idUser.forEach((element) => {
      this.socket.emit("join_rom", element);
    });
    this.socket.on("message_hi", (message) => {
      let { arr } = this.state;
      let findUserName = arr.find((arr) => arr.username === message.username);
      if (findUserName) {
        csl
      } else {
        arr.push(message);
      }
      this.setState({
        arr: arr,
      });
    });
  }
  handleOnChange = (e) => {
    this.setState({
      message: e.target.value,
    });
  };
  hanleOnClick = (e) => {
    e.preventDefault();
    this.socket.emit("send_mess", {
      mess: this.state.message,
      id: this.state.id,
    });
  };
  joinRoom = (e) => {
    this.socket.emit("join_rom", e.id);
    this.setState(
      {
        id: e.id,
      },
      () => {
        console.log(this.state.id);
      }
    );
  };
  render() {
    console.log(this.state.arr);

    const rows = [...this.props.listUser];
    const columns = [
      { field: "id", headerName: "ID", width: 70, headerAlign: "center" },
      {
        field: "username",
        headerName: "Username",
        width: 100,
      },
      {
        field: "email",
        headerName: "Email",
        width: 200,
      },
      {
        field: "role",
        headerName: "Role",
        width: 100,
      },
      {
        field: "password",
        headerName: "Password",
        width: 100,
      },
      {
        field: "address",
        headerName: "Address",
        width: 100,
        type: "text",
      },
      {
        field: "numberphone",
        headerName: "Number Phone",
        width: 100,
        type: "text",
      },
      {
        field: "button",
        headerName: "Action",
        width: 200,

        renderCell: (cellValues) => {
          return (
            <>
              <Button
                className="edit"
                variant="contained"
                color="success"
                onClick={() => this.joinRoom(cellValues)}
              >
                SEND MESSAGE
              </Button>
            </>
          );
        },
        headerAlign: "center",
      },
    ];
    return (
      <div className="container">
        <div className="form__chat">
          <div className="tool">
            <TextField onChange={this.handleOnChange} className="textF" />
            <Button
              onClick={this.hanleOnClick}
              color="success"
              variant="contained"
            >
              SEND MESSAGE
            </Button>
          </div>
        </div>
        <>
          <div className="data">
            <DataGrid rows={rows} columns={columns} pageSize={4} />
          </div>
        </>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return { listUser: state.listUser };
};
const mapDispatchToProps = (dispatch) => {
  return {
    getAllUser: () => {
      dispatch(actions.getAllUser());
    },
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(Chat);
